YUI.add("y3d-geometry-grid",function(e,t){e.Grid=e.Base.create("grid",e.Geometry,[],{initializer:function(){var e=this,t=e.get("size"),n=e.get("vertices"),r=e.get("normals"),i=e.get("lines"),s=t/2,o,u,a,f,l;for(f=0;f<=t;f++)l=12*f,o=s,u=0,a=-s+f,n[l]=-o,n[l+1]=u,n[l+2]=a,n[l+3]=o,n[l+4]=u,n[l+5]=a,o=-s+f,a=s,n[l+6]=o,n[l+7]=u,n[l+8]=-a,n[l+9]=o,n[l+10]=u,n[l+11]=a,l=4*f,i[l]=l,i[l+1]=l+1,i[l+2]=l+2,i[l+3]=l+3;for(f=0;f<n.length/3;f++)r.push(0,1,0);e.set("color","white")}},{ATTRS:{size:{value:12},wireframe:{value:!0}}})},"0.1",{requires:["y3d-geometry-base"]});
